INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (1, 'Salix alba cv. tristis', 'Le Saule blanc, Saule commun, Saule argenté, Osier blanc, ou Saule Vivier (Salix alba L.), est un arbre de la famille des Salicacées. Cet arbre commun dans les régions tempérées est souvent cultivé en forme de « têtard » pour la production d''osier.', 50.669303, 4.620818, 'Saule blanc pleureur', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (2, 'Acer pensylvanicum', 'L’érable de Pennsylvanie ou érable jaspé (Acer pensylvanicum) est l''unique «érable à peau de serpent» qui ne soit pas originaire d''Asie mais du nord-est de l’Amérique du Nord (et principalement de Pennsylvanie). Il appartient à la section Macrantha de la classification des érables et a été introduit en Europe en 1755. L’équivalent asiatique est Acer tegmentosum.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (3, 'Cupressus Sempervirens', 'Cupressus sempervirens, encore appelé cyprès commun, cyprès sempervirent, cyprès toujours vert, cyprès d''Italie, cyprès de Provence, ou encore cyprès méditerranéen est un arbre de la famille des Cupressaceae.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (4, 'Carya illinonensis', 'Le pacanier ou noyer de pécan (Carya illinoinensis) est un arbre de la famille des Juglandacées, cultivé pour son fruit (la noix de pécan ou pacane) principalement en Amérique du Nord d''où il est originaire.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (5, 'Taxus baccata', 'L’if commun (ou if à baies), (Taxus baccata), est une espèce de conifères non résineux de la famille des Taxaceae. Très longévif, poussant lentement, c’est un arbre qui se prête bien à la taille. Il est parfois appelé if à baies.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (6, 'Picea omorika', 'L''Épicéa de Serbie (Picea omorika), en serbe cyrillique панчићева оморика et en serbe translittéré pančićeva omorika, est une espèce d''épicéa endémique d''une petite région des Balkans.', 0, 0, 'Épicéa de Serbie', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (7, 'Ulmus minor', 'L’Orme champêtre, l’Ormeau ou le Petit Orme (Ulmus minor) est une espèce d''arbres à feuilles caduques de la famille des ulmacées. Il est parfois appelé Ipréau, Orme cilié ou Yvet.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (8, 'Acer capillipes', 'L''Érable jaspé de rouge est une espèce d''érable découverte en 1867 par Carl Johann Maximowicz (1827-1891) et introduite en Europe en 1892. Elle appartient à la section Macrantha de la classification des érables. Originaire d''Asie (forêts montagneuses du Japon principalement), c''est une des espèces d''érable les plus vigoureuses, résistantes au froid et qui ne nécessitent quasiment aucun entretien particulier. Ce type d''arbre apprécie les zones humides, les sols drainés et légers et une exposition ombragée. Son écorce striée vert et blanc caractéristique classe Acer capillipes dans la catégorie des érables à peau de serpent. Ses feuilles à pétioles rouges ont trois lobes pointus et peuvent mesurer jusqu''à 15 cm de long sur 10 cm de large. Vertes au printemps, elles passent au rouge orangé en automne.', 50.668921, 4.622273, 'Érable jaspé de rouge', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (9, 'Quercus robur cv. fastigiata', 'Le chêne pédonculé (Quercus robur L., 1753) est une espèce d''arbres à feuillage caduc originaire des régions tempérées d''Europe, appartenant à la famille des Fagacées. Son fruit est porté par un long pédoncule.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (10, 'Ulmus minor cv. variegata', 'L’Orme champêtre, l’Ormeau ou le Petit Orme (Ulmus minor) est une espèce d''arbres à feuilles caduques de la famille des ulmacées. Il est parfois appelé Ipréau, Orme cilié ou Yvet.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (11, 'Parrotia persica', 'Le nom du genre Metasequoia vient du grec meta (=proche de) et de sequoia, un genre de grands arbres de la même famille. L''épithète spécifique glyptostroboides signifie « semblable au Glyptostrobus ». Glyptostrobus pensilis est un cyprès chinois des marais, également originaire de Chine.', 50.668347, 4.621765, 'Parrotie de Perse', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (12, 'Carpinus betulus', 'Le Charme, ou Charme commun, Carpinus betulus, parfois appelé charmille, est un arbre de taille moyenne, à feuilles caduques et appartenant à la famille des Bétulacées. Il est assez répandu dans les forêts d''Europe centrale. C''est une essence forestière secondaire présente en taillis sous futaie.', 50.668466, 4.623166, 'Charme commun', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (13, 'Corylus avellana', 'Le Noisetier ou Coudrier (Corylus avellana) est un arbrisseau de 5 à 8 mètres de hauteur et appartenant au genre Corylus et à la famille des Bétulacées. Les 15 espèces du genre Corylus qui donnent toutes des fruits à coque contenant une amande comestible, sont aussi nommées en français « noisetier ». Deux espèces de Corylus sont indigènes en Europe, le Corylus avellana et le Corylus colurna, le « noisetier de Byzance », présent dans les Balkans, la Roumanie et la Turquie. Pour distinguer le Corylus avellana des autres, on l’appelle généralement « noisetier commun » ou « noisetier européen ».
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (14, 'Juglans nigra', 'Le Noyer d''Amérique ou Noyer noir (Juglans nigra) est un grand arbre de la famille des Juglandacées, originaire d''Amérique du Nord, largement cultivé pour ses fruits, son bois et ses qualités ornementales1. Il est introduit un peu partout dans le monde. Les deux pays européens qui comprennent les plus grandes superficies plantées en noyer noir sont la Hongrie et la Roumanie.', 50.668475, 4.625003, 'Noyer noir', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (15, 'Zone de Fagus sylvatica', 'Le Hêtre commun, Fagus sylvatica, couramment désigné simplement comme le hêtre est une espèce d''arbres à feuilles caduques, indigène d''Europe, appartenant à la famille des Fagaceae, tout comme le chêne et le châtaignier.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (16, 'Quercus robur - arbre de la continuité', 'Le chêne pédonculé (Quercus robur L., 1753) est une espèce d''arbres à feuillage caduc originaire des régions tempérées d''Europe, appartenant à la famille des Fagacées. Son fruit est porté par un long pédoncule.', 50.667956, 4.624662, 'Chêne pédonculé', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (17, 'Salix alba', 'Le Saule blanc, Saule commun, Saule argenté, Osier blanc, ou Saule Vivier (Salix alba L.), est un arbre de la famille des Salicacées. Cet arbre commun dans les régions tempérées est souvent cultivé en forme de « têtard » pour la production d''osier.', 50.667403, 4.623322, 'Saule blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (18, 'Populus nigra subsp. pyramidalis', 'Le peuplier noir (Populus nigra) est une espèce d''arbres de la famille des Salicacées. Ce peuplier de grande taille colonise de préférence les sols riches et dénués de végétation, tels que les grèves et bancs de sable humides. Il en existe de nombreux hybrides ou variétés, notamment le Peuplier d''Italie (Populus nigra var. Italica), à port érigé et étroit, planté comme arbre d''ornement ou d''alignement. La souplesse des fibres se prête bien à la fabrication du bois déroulé pour le placage et le contreplaqué. La plante a aussi des propriétés médicinales et sert à faire du charbon actif.', 50.66728, 4.622441, 'Peuplier noir d''Italie', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (19, 'Robinia pseudoacacia cv. Frisia', 'Le robinier faux-acacia ou acacia (Robinia pseudoacacia) est une espèce de la famille des Fabacées (légumineuses de la sous-famille des Viciaceae). Cet arbre présente des fleurs zygomorphes caractéristiques chez les Fabacées. Ses fruits sont des gousses ressemblant à un haricot plat avec des graines à l''intérieur. ''Frisia'' : à feuille plus étroite et restant jaune vif au printemps ; exposition protégée du vent. Arbre de petite à moyenne stature ; épines rouge.', 50.666781, 4.622288, 'Robinier faux-acacia cv. Frisia (acacier doré)', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (20, 'Robinia pseudoacacia', 'Le robinier faux-acacia ou acacia (Robinia pseudoacacia) est une espèce de la famille des Fabacées (légumineuses de la sous-famille des Viciaceae). Cet arbre présente des fleurs zygomorphes caractéristiques chez les Fabacées. Ses fruits sont des gousses ressemblant à un haricot plat avec des graines à l''intérieur.', 50.666506, 4.62254, 'Robinier faux-acacia', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (21, 'Sorbus aucuparia', 'Le Sorbier des oiseleurs ou Sorbier des oiseaux (Sorbus aucuparia L.) est un petit arbre qui peut atteindre les 5 mètres de haut. Il vit principalement en lisière de forêts d''épicéas. Le sorbier est cousin avec le cormier. Ses feuilles sont composées de 17 ou 15 folioles lancéolées doublement dentées reliées au pétiole.', 50.665647, 4.621641, 'Sorbier des oiseaux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (22, 'Ginkgo biloba', 'L’Arbre aux quarante écus, l’Abricotier d''argent, Ginkgo (Ginkgo biloba L., 1771) (银杏 yínxìng en chinois) est une espèce d''arbres et la seule représentante actuelle de la famille des Ginkgoaceae. C''est la seule espèce actuelle de la division des ginkgophyta. Elle est considérée comme une espèce panchroniquenote. C''est la plus ancienne famille d''arbres connue, puisqu''elle serait apparue il y a plus de 270 millions d''années. Elle existait déjà une quarantaine de millions d''années avant l''apparition des dinosaures.', 0, 0, 'Abricotier d''argent', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (23, 'Crataegus crus-galli', 'L''aubépine ergot de Coq (Crataegus crus-galli), aussi appelée "aubépine de Virginie", est une espèce végétale de la famille des Rosaceae. C''est une aubépine originaire de l''Est de l''Amérique du Nord, du Québec à la Louisiane.', 50.665508, 4.621451, 'Aubépine ergot de coq', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (24, 'Sequoiadendron giganteum', 'Le Séquoia géant (Sequoiadendron giganteum) est une espèce de conifères de la famille des Taxodiaceae (classification classique) ou des Cupressaceae (classification phylogénétique). Il est l''unique représentant actuel du genre Sequoiadendron.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (25, 'Alnus incana', 'L''aulne blanc, aulne rugueux, aulne de montagne, arcoce ou varne est un arbre, de la famille des bétulacées, originaire des régions tempérées de l''hémisphère nord.', 50.665188, 4.621086, 'Aulne blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (26, 'Tilia tomentosa', 'Le tilleul argenté (Tilia tomentosa), encore appelé Tilleul de Hongrie, est un arbre de la famille des Tiliaceae, ou des Malvaceae, sous-famille des Tilioideae, selon la classification phylogénétique. C''est une espèce originaire de l''est du bassin méditerranéen. Résistant bien à la pollution, il est souvent employé comme arbre d''alignement dans les villes.', 50.665438, 4.621006, 'Tilleul argenté', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (27, 'Tilia mongolica', 'Tilia mongolica Maxim., le Tilleul de Mongolie, a été découvert par le Père David en 1864 et introduit en occident en 1880. L''arbre vient de Mongolie, de l''est de la Russie et du nord de la Chine. Il pousse entre 1 200 et 2 200  m d''altitude.', 50.66571, 4.62081, 'Tilleul de Mongolie', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (28, 'Tilia cordata', 'Le Tilleul à petites feuilles (Tilia cordata Mill.) est un arbre du genre Tilia et de la famille des Tiliaceae, ou des Malvaceae selon la classification phylogénétique.', 0, 0, 'Tilleul à petites feuilles', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (29, 'Zelkova serrata', 'Le zelkova du Japon (Zelkova serrata) est un arbre caducifolié aux feuilles dentelées lancéolées de la famille des Ulmaceae. Il est appelé keyaki (欅, ケヤキ) au Japon. Philipp Franz von Siebold, l''a introduit aux Pays-Bas en 1830.', 50.665418, 4.620442, 'Zelkova du Japon', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (30, 'Populus x interamericana cv. UNAL', 'Les peupliers sont des arbres du genre Populus de la famille des Salicacées.', 50.665071, 4.620126, 'Peuplier interaméricain cv. Unal', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (31, 'Fagus sylvatica cv. laciniata', 'Le Hêtre commun, Fagus sylvatica, couramment désigné simplement comme le hêtre est une espèce d''arbres à feuilles caduques, indigène d''Europe, appartenant à la famille des Fagaceae, tout comme le chêne et le châtaignier.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (32, 'Betula pendula', 'Le bouleau verruqueux ou bouleau blanc (Betula pendula, syn. B. verrucosa), ou encore bouleau d''Europe, mais aussi bouleau blanc d''Europe est un arbre pouvant atteindre 25 mètres de hauteur. Originaire d''Europe et d''Asie, c''est un arbre très rustique et qui a besoin de lumière. Il aime les sols secs à frais, siliceux de préférence. Les sols acides à alcalins ne le dérangent pas outre mesure. C''est une essence pionnière, colonisatrice et très adaptable. Il résiste parfaitement aux embruns. Il peut être isolé, en alignement, bosquet, etc.', 50.665795, 4.620305, 'Bouleau verruqueux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (33, 'Juniperus chinensis cv. Hetzii', 'Le genévrier de Chine (Juniperus chinensis) est une espèce d''arbres appartenant au genre Juniperus et à la famille des Cupressaceae. Il est originaire du continent asiatique', 50.665962, 4.620206, 'Genévrier de Chine cv. Hetzii', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (34, 'Metasequoia glyptostroboides', 'Metasequoia glyptostroboides, aussi appelé « sapin d''eau », est une espèce de grand arbre de la famille des Taxodiaceae, ou des Cupressaceae selon la classification phylogénétique. Il est originaire de Chine où il a été découvert en 1943. C''est l''unique espèce vivante du genre Metasequoia dont l''origine remonte au Pliocène et à ce titre est considéré comme une forme panchronique.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (35, 'Crataegus x lavallei', 'Crataegus × lavalleei, parfois connu sous d''aubépine de Lavallée, est un hybride qui est né entre des spécimens cultivés de deux espèces du genre Crataegus (Aubépine). C''est un petit arbre ornemental à feuilles non lobées vert foncé brillant qui est utilisé en horticulture.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (36, 'Liquidambar styraciflua', 'Le copalme d''Amérique ou Liquidambar (Liquidambar styraciflua) est une espèce d''arbres qui appartient maintenant à la famille des Altingiacées, auparavant classé dans la famille des Hamamelidacées.', 50.666495, 4.61986, 'Copalme d''Amérique', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (37, 'Acer platanoides', 'L''Érable plane (Acer platanoides L.) est un arbre de grande taille de la famille des Sapindaceae fréquent dans les régions montagneuses d''Europe. Il est parfois appelé Érable de Norvège, Iseron, Plane, Main-découpée, Plaine ou Faux Sycomore.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (38, 'Acer campestre', 'L''Érable champêtre (Acer campestre) est un arbre du genre Acer appartenant à la famille des Acéracées (Sapindaceae APG III). Il est classé dans la section Platanoidea de la classification des érables.', 50.666978, 4.619667, 'Érable champêtre', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (39, 'Betula nigra cv. Héritage', 'Le Betula nigra (Bouleau noir) est une espèce de bouleau; il est commun dans les plaines alluviales et les marais de l''est des États-Unis, du New Hampshire au Minnesota, de la Floride à l''est du Texas.', 50.666915, 4.619495, 'Bouleau noir cv. Héritage', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (40, 'Betula pendula', 'Le bouleau verruqueux ou bouleau blanc (Betula pendula, syn. B. verrucosa), ou encore bouleau d''Europe, mais aussi bouleau blanc d''Europe est un arbre pouvant atteindre 25 mètres de hauteur. Originaire d''Europe et d''Asie, c''est un arbre très rustique et qui a besoin de lumière. Il aime les sols secs à frais, siliceux de préférence. Les sols acides à alcalins ne le dérangent pas outre mesure. C''est une essence pionnière, colonisatrice et très adaptable. Il résiste parfaitement aux embruns. Il peut être isolé, en alignement, bosquet, etc.', 0, 0, 'Bouleau verruqueux', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (41, 'Pinus nigra ssp. Nigricans', 'Le Pin noir (Pinus nigra) est une espèce de pin présentant de nombreuses variétés et que l''on trouve en Europe méridionale, de l''Espagne à la Crimée, en Asie Mineure, à Chypre et, localement, dans les montagnes de l''Atlas en Afrique du Nord-Ouest.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (42, 'Pinus nigra ssp. Nigricans', 'Le Pin noir (Pinus nigra) est une espèce de pin présentant de nombreuses variétés et que l''on trouve en Europe méridionale, de l''Espagne à la Crimée, en Asie Mineure, à Chypre et, localement, dans les montagnes de l''Atlas en Afrique du Nord-Ouest.', 0, 0, 'Pin noir ssp. d''Autriche', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (43, 'Pinus nigra ssp. Nigricans', 'Le Pin noir (Pinus nigra) est une espèce de pin présentant de nombreuses variétés et que l''on trouve en Europe méridionale, de l''Espagne à la Crimée, en Asie Mineure, à Chypre et, localement, dans les montagnes de l''Atlas en Afrique du Nord-Ouest.', 50.666822, 4.618967, 'Pin noir ssp. d''Autriche', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (44, 'Liriodendron tulipifera cv. aureomarginatum', 'Le tulipier de Virginie ou arbre aux lis est un arbre feuillu de la famille des Magnoliacées originaire du sud et de l''est des États-Unis.', 0, 0, 'Tulipier de Virginie cv. Aureomarginatum', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (45, 'Quercus macrocarpa', 'Le Chêne à gros fruits (Quercus macrocarpa) est une espèce d''arbres présente en Amérique du Nord aux États-Unis et au Canada. Il a été décrit pour la première fois par le botaniste André Michaux dans son Histoire des chênes de l''Amérique septentrionale. Malgré son nom vernaculaire et son nom scientifique, ce n’est pas l’espèce qui produit les plus gros fruits dans le genre.', 50.666919, 4.618769, 'Chêne à gros fruits', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (46, 'Platanus x acerifolia', 'Le platane commun ou platane à feuilles d''érable est une espèce hybride d''arbres de la famille des Platanaceae. C''est un hybride entre le platane d''Occident (Amérique du Nord) et le platane d''Orient (ouest de l''Asie, sud est de l''Europe), à fertilité très faible (seule une infime partie des graines peuvent germer), apparu au cours du xviiie siècle en Europe. Cette espèce est couramment utilisée comme arbre d''ornement et d''alignement le long des rues.', 0, 0, 'Platane à feuilles d''érable', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (47, 'Sorbus aria', 'L''Alisier blanc, Alisier de Bourgogne, Alouchier ou Sorbier des Alpes (Sorbus aria) est un arbre de la famille des Rosacées originaire du sud de l''Europe.', 50.668369, 4.618694, 'Sorbier blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (48, 'Carpinus betulus cv. fastigiata', 'Le Charme, ou Charme commun, Carpinus betulus, parfois appelé charmille, est un arbre de taille moyenne, à feuilles caduques et appartenant à la famille des Bétulacées. Il est assez répandu dans les forêts d''Europe centrale. C''est une essence forestière secondaire présente en taillis sous futaie.', 50.666331, 4.623486, 'Charme commun fastigié', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (49, 'Acer negundo', 'L''Érable negundo ou Érable négondo (Acer negundo) est une espèce végétale de la famille des Aceraceae. Cet érable d’une dizaine de mètres de hauteur est originaire de l’est de l''Amérique du Nord. On l''appelle aussi Érable à giguère, peut-être par déformation du nom « érable argilière » qu''utilisaient les Français de l''Illinois, en 1814. Il est appelé parfois Érable à feuilles de frêne ou Érable américain. Cette essence peu intéressante pour son bois a été introduite sur d''autres continents en tant qu''arbre d''ornement pour les parcs et jardins. Elle s''est révélée être localement envahissante.', 50.666532, 4.623501, 'Érable negundo', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (50, 'Aesculus hippocastanum', 'Le marronnier commun, marronnier d''Inde ou marronnier blanc (Aesculus hippocastanum L.) est un arbre de la famille des Hippocastanaceae (Sapindaceae en classification APGIII). Il est parfois appelé châtaignier de mer, marronnier faux-châtaignier ou châtaignier des chevaux.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (51, 'Acer palmatum cv. dissectum "Filigree"', 'L’Érable palmé (Acer palmatum), ou Érable japonais lisse, est une espèce indigène en Chine, en Corée, au Japon et à Taiwan. Il appartient à la famille des Sapindaceae et à la section Palmata de la classification des érables. Cette espèce d’arbre est souvent plantée dans les jardins pour ses formes et ses couleurs variées. Son nom vernaculaire érable japonais lisse, provient de la forme caractéristique fortement découpée de ses feuilles et de l''origine géographique de sa classification par le naturaliste suédois, Carl Peter Thunberg lors d''un voyage au Japon.', 0, 0, 'Érable palmé cv. Dissectum "Filigree"', 0);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (52, 'Ailanthus glandulosa', 'L''Ailante glanduleux, Ailante ou Faux vernis du Japon ou Vernis de Chine (Ailanthus altissima) est une espèce d''arbres à feuilles caduques de la famille des Simaroubaceae. Il est natif à la fois du nord-est et du centre de la Chine et de Taïwan. Il est présent davantage dans la forêt tempérée que dans la forêt subtropicale d’Extrême-Orient. L''arbre pousse vite et est capable d''atteindre des hauteurs de 15 mètres en 25 ans. Cependant, l''espèce a également une durée de vie courte et vit rarement plus de 50 ans (il peut cependant poursuivre son existence bien au-delà grâce à son pouvoir drageonnant particulièrement développé).
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (53, 'Tilia tomentosa', 'Le tilleul argenté (Tilia tomentosa), encore appelé Tilleul de Hongrie, est un arbre de la famille des Tiliaceae, ou des Malvaceae, sous-famille des Tilioideae, selon la classification phylogénétique. C''est une espèce originaire de l''est du bassin méditerranéen. Résistant bien à la pollution, il est souvent employé comme arbre d''alignement dans les villes.', 50.666339, 4.624095, 'Tilleul argenté', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (54, 'Catalpa bignonioides', 'Catalpa bignonioides, également appelé Arbre aux haricots, Catalpa boule ou Catalpa commun, est un arbre assez commun de la famille des Bignoniaceae.', 50.666232, 4.62409, 'Arbre aux haricots', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (55, 'Acer palmatum cv. dissectum', 'L’Érable palmé (Acer palmatum), ou Érable japonais lisse, est une espèce indigène en Chine, en Corée, au Japon et à Taiwan. Il appartient à la famille des Sapindaceae et à la section Palmata de la classification des érables. Cette espèce d’arbre est souvent plantée dans les jardins pour ses formes et ses couleurs variées. Son nom vernaculaire érable japonais lisse, provient de la forme caractéristique fortement découpée de ses feuilles et de l''origine géographique de sa classification par le naturaliste suédois, Carl Peter Thunberg lors d''un voyage au Japon.', 50.666129, 4.624005, 'Érable palmé cv. Dissectum', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (56, 'Acer campestre', 'L''Érable champêtre (Acer campestre) est un arbre du genre Acer appartenant à la famille des Acéracées (Sapindaceae APG III). Il est classé dans la section Platanoidea de la classification des érables.', 50.666093, 4.624187, 'Érable champêtre', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (57, 'Elaeagnus angustifolia', 'Elaeagnus angustifolia (olivier de Bohême, arbre d’argent, arbre du paradis, olinet, éléagne à feuilles étroites, chalef à feuilles étroites) est une espèce d''arbuste de la famille des Elaeagnaceae, largement répandu en Asie centrale et de l''ouest, au sud de la Russie, au Kazakhstan, en Turquie et en Iran. Ses fleurs parfumées, ses fruits comestibles et son aspect argenté lui ont valu d''être cultivé ailleurs dans le monde, mais cet Elaeagnus est devenu envahissant dans certains pays.', 50.666033, 4.624576, 'Olivier à feuilles étroites', 1);
