INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (69, 'Sequoidadendron giganteum', 'Le Séquoia géant (Sequoiadendron giganteum) est une espèce de conifères de la famille des Taxodiaceae (classification classique) ou des Cupressaceae (classification phylogénétique). Il est l''unique représentant actuel du genre Sequoiadendron.', 50.663229, 4.624373, 'Séquoia géant', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (70, 'Populus x canescens - groupe', 'Le peuplier grisard (Populus ×canescens) est une espèce hybride résultant du croisement entre le peuplier blanc et le peuplier tremble. On l''appelle également blanc de Hollande, ypréau ou franc-picard. Il vit au bord des étangs, des marais.', 50.663229, 4.624249, 'Peuplier grisard - groupe', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (71, 'Salix fragilis', 'Le Saule fragile ou Saule rouge (Salix fragilis) est un arbre de la famille des Salicaceae.', 50.6632, 4.624713, 'Saule fragile', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (72, 'Quercus rubra', 'Le chêne rouge d''Amérique (Quercus rubra L., syn. Quercus borealis Duroi) est un arbre appartenant à la section des chênes rouges (Lobatae) du genre Quercus de la famille des Fagacées. Il s''agit de l''arbre symbole de l''État du New Jersey. L’espèce est sans doute apparue il y a 7 millions d''années, mais les plus anciennes traces certaines datent d''il y a 7,5 millions d''années.', 50.664635, 4.622026, 'Chêne rouge d''Amérique', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (73, 'Acer pseudoplatanus', 'L’érable sycomore (Acer pseudoplatanus) est une espèce d''arbres de grande taille de la famille des acéracées fréquent en Europe. Il appartient à la section Acer de la classification des érables selon de Jong (1976).
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (74, 'Fagus sylvatica cv. tortuosa', 'Le Hêtre tortillard (Fagus sylvatica groupe Tortuosa) est un hêtre caractérisé par un tronc tortueux et des branches et rameaux tordus et retombants qui lui donnent un port particulier comme un parasol. La croissance d''un hêtre tortillard est très lente.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (75, 'Acer saccharinum', 'L’Érable argenté ou Érable de Virginie (Acer saccharinum), ou Plaine blanche (Canada), est une espèce d''arbre de la famille des Acéracées.', 50.666101, 4.622867, 'Érable argenté', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (76, 'Platanus x acerifolia ', 'Le platane commun ou platane à feuilles d''érable est une espèce hybride d''arbres de la famille des Platanaceae. C''est un hybride entre le platane d''Occident (Amérique du Nord) et le platane d''Orient (ouest de l''Asie, sud est de l''Europe), à fertilité très faible (seule une infime partie des graines peuvent germer), apparu au cours du xviiie siècle en Europe. Cette espèce est couramment utilisée comme arbre d''ornement et d''alignement le long des rues.', 50.666159, 4.623119, 'Platane à feuilles d''érable', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (77, 'Ginkgo biloba', 'L’Arbre aux quarante écus, l’Abricotier d''argent, Ginkgo (Ginkgo biloba L., 1771) (银杏 yínxìng en chinois) est une espèce d''arbres et la seule représentante actuelle de la famille des Ginkgoaceae. C''est la seule espèce actuelle de la division des ginkgophyta. Elle est considérée comme une espèce panchroniquenote. C''est la plus ancienne famille d''arbres connue, puisqu''elle serait apparue il y a plus de 270 millions d''années. Elle existait déjà une quarantaine de millions d''années avant l''apparition des dinosaures.', 50.66614, 4.623378, 'Abricotier d''argent', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (78, 'Quercus robur', 'Le chêne pédonculé (Quercus robur L., 1753) est une espèce d''arbres à feuillage caduc originaire des régions tempérées d''Europe, appartenant à la famille des Fagacées. Son fruit est porté par un long pédoncule.', 50.667785, 4.608924, 'Chêne pédonculé', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (79, 'Alnus glutinosa', 'L’aulne glutineux, l’aulne noir, l’aulne poisseux (Alnus glutinosa), est un arbre feuillu de la famille des bétulacées, très présent dans la flore indigène de l''Europe (Scandinavie comprise). Il est parfois appelé vergne ou verne.', 50.667042, 4.608587, 'Aulne glutineux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (80, 'Salix caprea', 'Le Saule marsault ou Saule des chèvres, Salix caprea est une espèce d''arbre ou d''arbuste de la famille des Salicaceae.', 50.666823, 4.607237, 'Saule marsault', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (81, 'Betula pendula', 'Le bouleau verruqueux ou bouleau blanc (Betula pendula, syn. B. verrucosa), ou encore bouleau d''Europe, mais aussi bouleau blanc d''Europe est un arbre pouvant atteindre 25 mètres de hauteur. Originaire d''Europe et d''Asie, c''est un arbre très rustique et qui a besoin de lumière. Il aime les sols secs à frais, siliceux de préférence. Les sols acides à alcalins ne le dérangent pas outre mesure. C''est une essence pionnière, colonisatrice et très adaptable. Il résiste parfaitement aux embruns. Il peut être isolé, en alignement, bosquet, etc.', 50.666826, 4.607159, 'Bouleau verruqueux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (82, 'Salix alba cv. tristis', 'Le Saule blanc, Saule commun, Saule argenté, Osier blanc, ou Saule Vivier (Salix alba L.), est un arbre de la famille des Salicacées. Cet arbre commun dans les régions tempérées est souvent cultivé en forme de « têtard » pour la production d''osier.', 50.666777, 4.606904, 'Saule blanc pleureur', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (83, 'Juglans nigra', 'Le Noyer d''Amérique ou Noyer noir (Juglans nigra) est un grand arbre de la famille des Juglandacées, originaire d''Amérique du Nord, largement cultivé pour ses fruits, son bois et ses qualités ornementales. Il est introduit un peu partout dans le monde. Les deux pays européens qui comprennent les plus grandes superficies plantées en noyer noir sont la Hongrie et la Roumanie', 50.666687, 4.606679, 'Noyer noir', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (84, 'Juglans regia', 'Le noyer commun ou noyer (Juglans regia L.), est un arbre assez commun, originaire d''Eurasie, cultivé pour son bois recherché en ébénisterie et ses fruits, les noix, riches en huile.
