INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (87, 'Liquidambar styraciflua', 'Le copalme d''Amérique ou Liquidambar (Liquidambar styraciflua) est une espèce d''arbres qui appartient maintenant à la famille des Altingiacées, auparavant classé dans la famille des Hamamelidacées.', 50.666289, 4.605941, 'Copalme d''Amérique', 1);
