INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (96, 'Carpinus betulus', 'Le Charme, ou Charme commun, Carpinus betulus, parfois appelé charmille, est un arbre de taille moyenne, à feuilles caduques et appartenant à la famille des Bétulacées. Il est assez répandu dans les forêts d''Europe centrale. C''est une essence forestière secondaire présente en taillis sous futaie.', 50.665627, 4.604242, 'Charme commun', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (97, 'Viburnum opulus', 'La viorne obier (Viburnum opulus) est un arbuste à feuilles caduques et palmées, appartenant à la famille des Adoxacées.', 50.664578, 4.604343, 'Viorne obier', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (98, 'Pterocarya fraxinifolia', 'Le Ptérocaryer du Caucase (Pterocarya fraxinifolia), aussi appelé Noyer du Caucase, est un arbre à feuilles caduques de la famille des Juglandaceae originaire des vallées du Caucase et de l''Elbourz.', 50.663832, 4.604421, 'Noyer du Caucase', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (99, 'Tilia americana', 'Le Tilleul d''Amérique (Tilia americana L.) est une espèce de feuillu de la famille des Tilleuls (famille des Tiliacées), originaire de l''est de l''Amérique du Nord.', 50.663825, 4.604182, 'Tilleul d''Amérique', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (100, 'Acer rufinerve cv. limbatum', 'Acer rufinerve aussi connu sous les noms d‘Érable à feuilles de vigne, Érable rufinerve, Érable oriental à bourgeons gris, Érable à écorce striée, Érable jaspé de gris ou Érable à peau de serpent est une espèce d’arbre du genre des érables. Originaire des forêts des montagnes du Japon, elle est cultivée dans d''autres régions du monde pour l''ornement.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (101, 'Alnus x spaethii (japonica x subcordata)', 'L’aulne de Spaeth (Alnus × spaethii) est un arbre de la famille des Betulaceae. C''est un aulne ornemental hybride, issu du croisement d’Alnus subcordata et d’Alnus japonica, obtenu à Berlin en 1908.', 50.663937, 4.603932, 'Aulne de Spaeth', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (102, 'Prunus avium', 'Le merisier ou cerisier des oiseaux (Prunus avium) est un arbre originaire d''Europe, d''Asie de l''ouest et d''Afrique du nord (Paléarctique occidental) appartenant au genre Prunus de la famille des Rosaceae. Il est parfois appelé cerisier sauvage ou cerisier des bois.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (103, 'Quercus velutina', 'Quercus velutina, le chêne noir, chêne des teinturiers ou Quercitron, est un chêne appartenant à la section des chênes rouges (Quercus sect. Lobatae), dans la famille des Fagaceae.', 50.664078, 4.603875, 'Chêne noir', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (104, 'Quercus palustris', 'Le Chêne des marais ou Chêne à épingles (Quercus palustris) est un arbre de la famille des Fagaceae originaire de l''Est des États-Unis d''Amérique et du Canada.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (105, 'Acer davidii', 'L''érable du Père David est une espèce d''érable appartenant à la section Macrantha de la classification des érables. Originaire de Chine, il appartient aussi à la catégorie des érables à peau de serpent.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (106, 'Sorbus torminalis', 'L''Alisier torminal, Alisier des bois ou Sorbier torminal (Sorbus torminalis L. Crantz) est un arbre originaire d''Europe, d''Afrique du Nord et d''Asie Mineure, appartenant au genre des sorbiers, à la famille des Rosacées et à la sous-famille des Maloideae.', 50.664182, 4.603749, 'Alisier torminal', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (107, 'Quercus x rosacea (Q.petraea x Q.robur)', 'Quercus x rosacea est une espèce hybride de chêne d''origine naturelle originaire d''Europe et peut-être d''Anatolie. C''est une progéniture naturelle de chêne sessile, Quercus petraea, et de chêne commun, Quercus robur, trouvée là où leurs aires de répartition se chevauchent. En tant qu''hybrides, ils sont morphologiquement variables, mais en général leurs traits semblent intermédiaires à ceux des parents. Une mince section d''un spécimen de Q. × rosacea a été utilisée par l''artiste en résidence Tania Kovats pour créer une œuvre monumentale appelée TREE pour le plafond de la mezzanine du Natural History Museum de Londres à l''occasion du bicentenaire de la naissance de Charles Darwin.', 50.664122, 4.603599, 'Chêne hybride', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (108, 'Ulmus x hollandica cv. Klemmer', ' Ulmus x hollandica, souvent connu simplement sous le nom d''orme hollandais, est un hybride naturel entre l''orme wych Ulmus glabra et l''orme champêtre (Ulmus minor) qui se rencontre couramment en Europe partout où les aires de répartition des espèces parentes se chevauchent. En Angleterre, selon les études sur le terrain de RH Richens, "La plus grande zone d''hybridation est une bande s''étendant à travers l''Essex de la frontière du Hertfordshire au sud du Suffolk. La deuxième plus grande se trouve dans le nord du Bedfordshire et les parties adjacentes du Northamptonshire Des zones comparables existent en Picardie et dans le Cotentin dans le nord de la France ".
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (109, 'Salix alba', 'Le Saule blanc, Saule commun, Saule argenté, Osier blanc, ou Saule Vivier (Salix alba L.), est un arbre de la famille des Salicacées. Cet arbre commun dans les régions tempérées est souvent cultivé en forme de « têtard » pour la production d''osier.', 50.664554, 4.603251, 'Saule blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (110, 'Alnus x spaethii (japonica x subcordata)', 'L’aulne de Spaeth (Alnus × spaethii) est un arbre de la famille des Betulaceae. C''est un aulne ornemental hybride, issu du croisement d’Alnus subcordata et d’Alnus japonica, obtenu à Berlin en 1908.', 50.665156, 4.602806, 'Aulne de Spaeth', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (111, 'Cornus alba cv. Sibirica', 'Cornus alba, le cornouiller blanc ou cornouiller de Tartarie, se présente sous la forme d''un grand buisson ou d''un arbuste. Il appartient à la famille des Cornaceae. Ses fleurs blanches apparaissent de fin mai à fin juillet.', 50.665379, 4.602586, 'Cornouiller blanc cv. Sibirica', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (112, 'Quercus alba', 'Le chêne blanc ou chêne blanc d''Amérique (Quercus alba) est une espèce de chêne mesurant en général une trentaine de mètres, le plus grand connu atteint les 44 mètres de hauteur. Certains spécimens vivent plus de 500 ans. On le trouve principalement en Amérique du Nord dans un climat continental humide. Cet arbre a presque disparu du Québec à cause de sa surexploitation passée, et de la perte de son habitat. Son bois a été utilisé pour la construction maritime, et peut prendre une flexion de 90 degrés. Le bois du chêne à gros fruits, aux propriétés semblables, est utilisé aux mêmes fins.', 50.666237, 4.602972, 'Chêne blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (113, 'Quercus palustris', 'Le Chêne des marais ou Chêne à épingles (Quercus palustris) est un arbre de la famille des Fagaceae originaire de l''Est des États-Unis d''Amérique et du Canada.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (114, 'Acer opalus', 'L''érable à feuilles d''obier, érable d''Italie ou érable Duret ou ayard (Acer opalus) est un arbre de taille moyenne (ne dépassant généralement pas 8 mètres de hauteur) de la famille des Acéracées selon la classification classique, ou de la famille des Sapindacées (à laquelle appartiennent dorénavant les Acéracées) selon la classification phylogénétique.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (115, 'Thuja plicata', 'Le Thuya géant (Thuja plicata) aussi appelé Cèdre rouge de l’Ouest et parfois Thuya de Lobb, est une espèce de conifère sempervirent de la famille des Cupressaceae originaire du nord-ouest de l''Amérique du Nord.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (116, 'Larix Kaempferi', 'Le mélèze du Japon (Larix kaempferi) est une espèce d''arbre résineux du genre Larix et de la famille des Pinaceae.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (117, 'Quercus robur', 'Le chêne pédonculé (Quercus robur L., 1753) est une espèce d''arbres à feuillage caduc originaire des régions tempérées d''Europe, appartenant à la famille des Fagacées. Son fruit est porté par un long pédoncule.', 50.667915, 4.604196, 'Chêne pédonculé', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (118, 'Castanea sativa', 'Le châtaignier commun (Castanea sativa Mill.) est un arbre à feuillage caduc de la famille des fagacées. Il produit des fruits : les châtaignes. Lorsqu''il est en nombre sur un territoire délimité, il forme une châtaigneraie.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (119, 'Robinia pseudoacacia', '''Le robinier faux-acacia ou acacia (Robinia pseudoacacia) est une espèce de la famille des Fabacées (légumineuses de la sous-famille des Viciaceae). Cet arbre présente des fleurs zygomorphes caractéristiques chez les Fabacées. Ses fruits sont des gousses ressemblant à un haricot plat avec des graines à l''intérieur.', 50.66794, 4.603729, 'Robinier faux-acacia', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (120, 'Salix viminalis', 'Le saule des vanniers, appelé aussi vime ou osier vert (Salix viminalis), est un saule d''une hauteur de 3 à 6 m. Il tire son nom de l''utilisation de ses rameaux souples en vannerie sous le nom d''osier. D''autres variétés de saule sont également utilisées en vannerie, comme le saule pourpre ou le saule blanc.', 50.667, 4.605003, 'Saule des vanniers', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (121, 'Alnus cordata', 'L''Aulne de Corse, Aulne cordé, ou Aulne à feuilles en cœur (Alnus cordata), parfois appelé Aulne d''Italie ou Aulne cordiforme est un arbre feuillu de la famille des Bétulacées, originaire du Sud de l''Europe (Italie, Corse).', 50.667238, 4.605497, 'Aulne de Corse', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (122, 'Salix alba', 'Le Saule blanc, Saule commun, Saule argenté, Osier blanc, ou Saule Vivier (Salix alba L.), est un arbre de la famille des Salicacées. Cet arbre commun dans les régions tempérées est souvent cultivé en forme de « têtard » pour la production d''osier.', 50.667269, 4.60563, 'Saule blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (123, 'Acer saccharinum', 'L’Érable argenté ou Érable de Virginie (Acer saccharinum), ou Plaine blanche (Canada), est une espèce d''arbre de la famille des Acéracées.', 50.667441, 4.60566, 'Érable argenté', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (124, 'Ulmus carpinifolia', 'L''orme à feuilles de charme est un arbre de la famille des Ulmacées. Principal prédateur : la graphiose de larme , c''est un insecte qui creuse sous le bois des galeries, ramenant ainsi une maladie', 50.667536, 4.605993, 'Orme à feuilles de charme', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (125, 'Acer pseudoplatanus', 'L’érable sycomore (Acer pseudoplatanus) est une espèce d''arbres de grande taille de la famille des acéracées fréquent en Europe. Il appartient à la section Acer de la classification des érables selon de Jong (1976).
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (126, 'Gleditsia triacanthos cv. Elegantissima', 'Gleditsia triacanthos, le févier d''Amérique, est une espèce de plantes dicotylédones de la famille des Fabaceae (légumineuses), sous-famille des Caesalpinioideae, originaire de l''est de l’Amérique du Nord. Ce sont des arbres de grande taille, épineux, à feuilles caduques.', 50.665397, 4.618177, 'Février d''Amérique cv. Elegantissima', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (127, 'Aesculus parviflora', 'Aesculus parviflora (Pavier blanc ou Marronnier nain) est un marronnier ornemental originaire d''Amérique, de la famille des Sapindacées.', 50.665527, 4.618634, 'Marronnier nain', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (128, 'Sequoiadendron giganteum cv. pygmaeum', 'Le Séquoia géant (Sequoiadendron giganteum) est une espèce de conifères de la famille des Taxodiaceae (classification classique) ou des Cupressaceae (classification phylogénétique). Il est l''unique représentant actuel du genre Sequoiadendron. Pygmaeum désigne un cultivar de cette espèce type, de forme naine.', 50.66552, 4.618776, 'Séquoia géant cv. pygmaeum', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (129, 'Carpinus betulus', 'Le Charme, ou Charme commun, Carpinus betulus, parfois appelé charmille, est un arbre de taille moyenne, à feuilles caduques et appartenant à la famille des Bétulacées. Il est assez répandu dans les forêts d''Europe centrale. C''est une essence forestière secondaire présente en taillis sous futaie.', 50.66534, 4.61881, 'Charme commun', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (130, 'Pseudotsuga menziesii  Var. menziesii Franco', 'Pseudotsuga menziesii est une espèce de conifère de la famille des Pinaceae, originaire de l''ouest de l''Amérique du Nord. En français on l’appelle douglas vert ou sapin de Douglas (bien qu''il ne s''agit pas d''un vrai sapin car il n''appartient pas au genre Abies), mais aussi pin d''Orégon et douglas de Menzies.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (131, 'Lindera benzoin', 'Lindera benzoin est un arbuste de la famille des Lauracées originaire d''Amérique du Nord.', 50.665235, 4.618481, 'Lindera benzoin', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (132, 'Salix alba', 'Le Saule blanc, Saule commun, Saule argenté, Osier blanc, ou Saule Vivier (Salix alba L.), est un arbre de la famille des Salicacées. Cet arbre commun dans les régions tempérées est souvent cultivé en forme de « têtard » pour la production d''osier.', 50.665242, 4.618378, 'Saule blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (133, 'Metasequoia glyptostroboides', 'Metasequoia glyptostroboides, aussi appelé « sapin d''eau », est une espèce de grand arbre de la famille des Taxodiaceae, ou des Cupressaceae selon la classification phylogénétique. Il est originaire de Chine où il a été découvert en 1943. C''est l''unique espèce vivante du genre Metasequoia dont l''origine remonte au Pliocène et à ce titre est considéré comme une forme panchronique.', 50.665361, 4.618394, 'Sapin d''eau', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (134, 'Pinus nigra ssp. Laricio Poiret', 'Le Pin noir (Pinus nigra) est une espèce de pin présentant de nombreuses variétés et que l''on trouve en Europe méridionale, de l''Espagne à la Crimée, en Asie Mineure, à Chypre et, localement, dans les montagnes de l''Atlas en Afrique du Nord-Ouest.', 50.665296, 4.618306, 'Pin noir ssp. Laricio Poiret', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (135, 'Taxus baccata', 'L’if commun (ou if à baies), (Taxus baccata), est une espèce de conifères non résineux de la famille des Taxaceae. Très longévif, poussant lentement, c’est un arbre qui se prête bien à la taille. Il est parfois appelé if à baies.', 50.66526, 4.618207, 'If commun', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (136, 'Abies grandis', 'Le Sapin de Vancouver ou Sapin géant (Abies grandis) est une espèce d’arbre de la famille des Pinaceae. C''est un sapin originaire d''Amérique du Nord (Oregon, Washington et Colombie-Britannique). Celui-ci a été introduit en Europe en 1831.', 50.665127, 4.618085, 'La sapin de Vancouver', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (137, 'Nyssa sylvatica', 'Nyssa sylvatica, communément appelé tupelo, tupelo noir, gomme noire, gommier noir ou gomme aigre1, est un arbre à feuilles caduques de taille moyenne originaire de l''est de l''Amérique du Nord, du nord-est côtier des États-Unis et du sud de l''Ontario jusqu''au centre de la Floride et de l''est du Texas, ainsi qu''au Mexique.', 50.665063, 4.618044, 'Tupelo', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (138, 'Acer negundo', 'L''Érable negundo ou Érable négondo (Acer negundo) est une espèce végétale de la famille des Aceraceae. Cet érable d’une dizaine de mètres de hauteur est originaire de l’est de l''Amérique du Nord. On l''appelle aussi Érable à giguère, peut-être par déformation du nom « érable argilière » qu''utilisaient les Français de l''Illinois, en 1814. Il est appelé parfois Érable à feuilles de frêne ou Érable américain. Cette essence peu intéressante pour son bois a été introduite sur d''autres continents en tant qu''arbre d''ornement pour les parcs et jardins. Elle s''est révélée être localement envahissante.', 50.665274, 4.617937, 'Érable negundo', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (139, 'Quercus robur', 'Le chêne pédonculé (Quercus robur L., 1753) est une espèce d''arbres à feuillage caduc originaire des régions tempérées d''Europe, appartenant à la famille des Fagacées. Son fruit est porté par un long pédoncule.', 50.665368, 4.617868, 'Chêne pédonculé', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (140, 'Cedrus libani', 'Le Cèdre du Liban (Cedrus libani), parfois appelé Cèdre du mont Liban, est une espèce d''arbres conifères de la famille des Pinaceae. La grande caractéristique du cèdre du Liban provient de son port conique durant ses trente premières années, devenant tabulaire par la suite.', 50.665509, 4.617896, 'Cèdre du Liban', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (141, 'Acer pseudoplatanus cv. Leopoldii', 'L’érable sycomore (Acer pseudoplatanus) est une espèce d''arbres de grande taille de la famille des acéracées fréquent en Europe. Il appartient à la section Acer de la classification des érables selon de Jong (1976).
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (142, 'Fraxinus longicuspis', 'Le frêne, arbre du genre Fraxinus, appartient à la famille des Oléacées ; une soixantaine d''espèces de frênes sont connues, elles vivent essentiellement dans les forêts tempérées. Caractérisées par des feuilles composées pennées, elles sont reconnaissables à leurs grappes de samares simples surnommées localement « langues d''oiseau ».
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (143, 'Sorbus aria', 'L''Alisier blanc, Alisier de Bourgogne, Alouchier ou Sorbier des Alpes (Sorbus aria) est un arbre de la famille des Rosacées originaire du sud de l''Europe.', 50.665373, 4.616291, 'Sorbier blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (144, 'Sorbus aria', 'L''Alisier blanc, Alisier de Bourgogne, Alouchier ou Sorbier des Alpes (Sorbus aria) est un arbre de la famille des Rosacées originaire du sud de l''Europe.', 50.665448, 4.616243, 'Sorbier blanc', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (145, 'Populus x interamericana UNAL', 'Les peupliers sont des arbres du genre Populus de la famille des Salicacées.', 50.664329, 4.615746, 'Peuplier interaméricain Unal', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (146, 'Liquidambar styraciflua', 'Le copalme d''Amérique ou Liquidambar (Liquidambar styraciflua) est une espèce d''arbres qui appartient maintenant à la famille des Altingiacées, auparavant classé dans la famille des Hamamelidacées.', 50.664177, 4.615258, 'Copalme d''Amérique', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (147, 'Aesculus turbinata', 'Le marronnier du Japon, de la famille des Hippocastanacées est une des espèces d''arbres d''alignement ou d''ornement répandu dans les parcs publics et le long des avenues dans l''hémisphère nord.', 50.663706, 4.615374, 'Marronnier du Japon', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (148, 'Betula pendula', 'Le bouleau verruqueux ou bouleau blanc (Betula pendula, syn. B. verrucosa), ou encore bouleau d''Europe, mais aussi bouleau blanc d''Europe est un arbre pouvant atteindre 25 mètres de hauteur. Originaire d''Europe et d''Asie, c''est un arbre très rustique et qui a besoin de lumière. Il aime les sols secs à frais, siliceux de préférence. Les sols acides à alcalins ne le dérangent pas outre mesure. C''est une essence pionnière, colonisatrice et très adaptable. Il résiste parfaitement aux embruns. Il peut être isolé, en alignement, bosquet, etc.', 50.6637, 4.615737, 'Bouleau verruqueux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (149, 'Betula pendula', 'Le bouleau verruqueux ou bouleau blanc (Betula pendula, syn. B. verrucosa), ou encore bouleau d''Europe, mais aussi bouleau blanc d''Europe est un arbre pouvant atteindre 25 mètres de hauteur. Originaire d''Europe et d''Asie, c''est un arbre très rustique et qui a besoin de lumière. Il aime les sols secs à frais, siliceux de préférence. Les sols acides à alcalins ne le dérangent pas outre mesure. C''est une essence pionnière, colonisatrice et très adaptable. Il résiste parfaitement aux embruns. Il peut être isolé, en alignement, bosquet, etc.', 50.663743, 4.615847, 'Bouleau verruqueux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (150, 'Betula pendula', 'Le bouleau verruqueux ou bouleau blanc (Betula pendula, syn. B. verrucosa), ou encore bouleau d''Europe, mais aussi bouleau blanc d''Europe est un arbre pouvant atteindre 25 mètres de hauteur. Originaire d''Europe et d''Asie, c''est un arbre très rustique et qui a besoin de lumière. Il aime les sols secs à frais, siliceux de préférence. Les sols acides à alcalins ne le dérangent pas outre mesure. C''est une essence pionnière, colonisatrice et très adaptable. Il résiste parfaitement aux embruns. Il peut être isolé, en alignement, bosquet, etc.', 50.66378, 4.616461, 'Bouleau verruqueux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (151, 'Betula pendula', 'Le bouleau verruqueux ou bouleau blanc (Betula pendula, syn. B. verrucosa), ou encore bouleau d''Europe, mais aussi bouleau blanc d''Europe est un arbre pouvant atteindre 25 mètres de hauteur. Originaire d''Europe et d''Asie, c''est un arbre très rustique et qui a besoin de lumière. Il aime les sols secs à frais, siliceux de préférence. Les sols acides à alcalins ne le dérangent pas outre mesure. C''est une essence pionnière, colonisatrice et très adaptable. Il résiste parfaitement aux embruns. Il peut être isolé, en alignement, bosquet, etc.', 50.663729, 4.616566, 'Bouleau verruqueux', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (152, 'Salix alba cv. tristis', 'Le Saule blanc, Saule commun, Saule argenté, Osier blanc, ou Saule Vivier (Salix alba L.), est un arbre de la famille des Salicacées. Cet arbre commun dans les régions tempérées est souvent cultivé en forme de « têtard » pour la production d''osier.', 50.663727, 4.617133, 'Saule blanc pleureur', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (153, 'Corylus maxima cv. purpurea', 'Le Noisetier de Lambert (Corylus maxima) est un arbuste de la famille des bétulacées. Sa variété pourpre est plantée comme arbuste d''ornement.', 50.6639, 4.616879, 'Noisetier de Lambert cv. Pourpre', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (154, 'Quercus cerris', 'Le Chêne chevelu ou Chêne lombard ou Chêne cerris (Quercus cerris), est une espèce d''arbres monoïques de la famille des Fagacées originaire du sud de l''Europe et d''Asie Mineure. Il est parfois appelé Chêne de Bourgogne, Chêne de Turquie ou Doucier.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (155, 'Ginkgo biloba', 'L’Arbre aux quarante écus, l’Abricotier d''argent, Ginkgo (Ginkgo biloba L., 1771) (银杏 yínxìng en chinois) est une espèce d''arbres et la seule représentante actuelle de la famille des Ginkgoaceae. C''est la seule espèce actuelle de la division des ginkgophyta. Elle est considérée comme une espèce panchroniquenote. C''est la plus ancienne famille d''arbres connue, puisqu''elle serait apparue il y a plus de 270 millions d''années. Elle existait déjà une quarantaine de millions d''années avant l''apparition des dinosaures.', 50.660583, 4.608787, 'Abricotier d''argent', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (156, 'Aesculus x carnea', 'Le Marronnier à fleurs rouge, Aesculus ×carnea, est une espèce d’arbres décoratifs de la famille des Hippocastanacées.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (157, 'Ulmus minor', 'L’Orme champêtre, l’Ormeau ou le Petit Orme (Ulmus minor) est une espèce d''arbres à feuilles caduques de la famille des ulmacées. Il est parfois appelé Ipréau, Orme cilié ou Yvet.', 50.66071, 4.608406, 'Orme champêtre', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (158, 'Sequoiadendron giganteum', 'Le Séquoia géant (Sequoiadendron giganteum) est une espèce de conifères de la famille des Taxodiaceae (classification classique) ou des Cupressaceae (classification phylogénétique). Il est l''unique représentant actuel du genre Sequoiadendron.', 50.660592, 4.60794, 'Séquoia géant', 1);
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (159, 'Quercus dentata cv. yunnanensis', 'Quercus dentata, également appelé chêne empereur japonais ou chêne daimyo (japonais: 柏, kashiwa; chinois simplifié: 柞 栎; chinois traditionnel: 柞 櫟; pinyin: zuòlì; coréen: 떡갈 나무, tteokgalnamu) est une espèce de chêne originaire du Japon, de Corée et de Chine. Le nom de l''arbre est souvent traduit par "chêne doux" en anglais pour le distinguer des variétés occidentales.
INSERT INTO devWeb_Project_Bdd.Points (idPoint, namePoint, descriptionPoint, latitudePoint, longitudePoint, vernaculairePoint, disponiblePoint) VALUES (160, 'Robinia pseudoacacia', '''Le robinier faux-acacia ou acacia (Robinia pseudoacacia) est une espèce de la famille des Fabacées (légumineuses de la sous-famille des Viciaceae). Cet arbre présente des fleurs zygomorphes caractéristiques chez les Fabacées. Ses fruits sont des gousses ressemblant à un haricot plat avec des graines à l''intérieur.', 50.660983, 4.606725, 'Robinier faux-acacia', 1);
